function ocultaElemento(){document.querySelector("header").classList.remove("header--is-hidden")}function exbibeElemento(){document.querySelector(".header").classList.add("header--is-hidden")}document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?ocultaElemento:exbibeElemento)()})});